    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
        MotursaDAO md = new MotursaDAO();
        EHMotursa em = new EHMotursa();

        if (txtNit.getText().isEmpty() || txtName.getText().isEmpty()
                || txtDirec.getText().isEmpty() || txtTel.getText().isEmpty()
                || txtSal.getText().isEmpty() || txtDesR.getText().isEmpty()) {

            JOptionPane.showMessageDialog(null, "Todos los campos son requeridos");

        } else if (md.consultar(txtNit.getText()) != null) {
            JOptionPane.showMessageDialog(null, "Nit ya registrado ");
        } else {

            em.setNoNit(txtNit.getText());
            em.setNombre(txtName.getText());
            em.setDireccion(txtDirec.getText());
            em.setTelefono(txtTel.getText());
            em.setSalarioMensual(Double.parseDouble(txtSal.getText()));
            em.setDecuentoRenta(Double.parseDouble(txtDesR.getText()));
            md.guardar(em);

            String data = "Usuario Creado con exito\n\n"
                    + "No Nit: " + txtNit.getText().toUpperCase() + "\n"
                    + "Nombre: " + txtName.getText().toUpperCase() + "\n"
                    + "Dirección: " + txtDirec.getText().toUpperCase() + "\n"
                    + "Teléfono: " + txtTel.getText().toUpperCase() + "\n"
                    + "Salario Mensual" + txtSal.getText().toUpperCase() + "\n"
                    + "Descuento Renta: " + txtDesR.getText().toUpperCase() + "\n";

            JOptionPane.showMessageDialog(null, data);
        }
    }//GEN-LAST:event_btnSaveActionPerformed

