    private void btnUpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUpdateActionPerformed
        MotursaDAO md = new MotursaDAO();
        EHMotursa em = new EHMotursa();

        try {
            if (txtNit.getText().isEmpty() || txtName.getText().isEmpty()
                    || txtDirec.getText().isEmpty() || txtTel.getText().isEmpty()
                    || txtSal.getText().isEmpty() || txtDesR.getText().isEmpty()) {

                JOptionPane.showMessageDialog(null, "Todos los campos son requeridos");
            } else {

                em.setNombre(txtName.getText());
                em.setDireccion(txtDirec.getText());
                em.setTelefono(txtTel.getText());
                em.setSalarioMensual(Double.parseDouble(txtSal.getText()));
                em.setDecuentoRenta(Double.parseDouble(txtDesR.getText()));
                em.setNoNit(txtNit.getText());
                md.modificar(em);

                String data = "Usuario Actualizado\n\n"
                        + "No Nit: " + txtNit.getText().toUpperCase() + "\n"
                        + "Nombre: " + txtName.getText().toUpperCase() + "\n"
                        + "Dirección: " + txtDirec.getText().toUpperCase() + "\n"
                        + "Teléfono: " + txtTel.getText().toUpperCase() + "\n"
                        + "Salario Mensual" + txtSal.getText().toUpperCase() + "\n"
                        + "Descuento Renta: " + txtDesR.getText().toUpperCase() + "\n";

                JOptionPane.showMessageDialog(null, data);
            }

        } catch (NullPointerException ex) {
            ex.printStackTrace();
            System.out.println("erro testeo modal" + ex.getMessage());
        } catch (Exception e) {
        }
    }//GEN-LAST:event_btnUpdateActionPerformed

    private void btnReadActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnReadActionPerformed
        MotursaDAO md = new MotursaDAO();
        EHMotursa em = md.consultar(txtNit.getText());

        if (em == null) {
            txtNit.setText("Digite un Usuario");

            JOptionPane.showMessageDialog(null, "Usuario no existe ");
            txtNit.setText("");
        } else if (em != null) {
            txtNit.setText(em.getNoNit());
            txtName.setText(em.getNombre());
            txtDirec.setText(em.getDireccion());
            txtTel.setText(em.getTelefono());
            txtSal.setText(String.valueOf(em.getSalarioMensual()));
            txtDesR.setText(String.valueOf(em.getDecuentoRenta()));
            String data = "Datos del Usuario\n\n"
                    + "No Nit: " + txtNit.getText().toUpperCase() + "\n"
                    + "Nombre: " + txtName.getText().toUpperCase() + "\n"
                    + "Dirección: " + txtDirec.getText().toUpperCase() + "\n"
                    + "Teléfono: " + txtTel.getText().toUpperCase() + "\n"
                    + "Salario Mensual" + txtSal.getText().toUpperCase() + "\n"
                    + "Descuento Renta: " + txtDesR.getText().toUpperCase() + "\n";

            JOptionPane.showMessageDialog(null, data);

        }
    }//GEN-LAST:event_btnReadActionPerformed

    private void btnDeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDeleteActionPerformed
        MotursaDAO md = new MotursaDAO();
        EHMotursa em = md.consultar(txtNit.getText());

        if (txtNit.getText().isEmpty() || md.consultar(txtNit.getText()) == null) {
            txtNit.setText("Digite un Usuario");
        } else {

            txtName.setText(em.getNombre());
            txtDirec.setText(em.getDireccion());
            txtTel.setText(em.getTelefono());
            txtSal.setText(String.valueOf(em.getSalarioMensual()));
            txtDesR.setText(String.valueOf(em.getDecuentoRenta()));

            String opcion = JOptionPane.showInputDialog("¿Desea eliminar el usuario permanentemente? \n"
                    + "Si S / No N").toUpperCase();
            if (opcion.equals("S")) {
                JOptionPane.showMessageDialog(null, "Usuario eliminado");
                md.eliminar(txtNit.getText());
            } else {
                JOptionPane.showMessageDialog(null, "Usuario NO eliminado");
            }
        }
    }//GEN-LAST:event_btnDeleteActionPerformed

